const thumbnails=document.querySelectorAll(".image-thumbnail"),mainImage=document.querySelector(".product-img img");function changeMainImage(t){thumbnails.forEach((t=>t.classList.remove("active")));const e=t.currentTarget;e.classList.add("active");const n=e.querySelector("img").src;mainImage.setAttribute("src",n)}thumbnails.forEach((t=>{t.addEventListener("click",changeMainImage)}));const quantityInput=document.querySelector(".quantity-input");function updateQuantity(t){let e=parseInt(quantityInput.value);t?quantityInput.value=e+1:e>1&&(quantityInput.value=e-1)}function updateCartCount(t){const e=document.querySelector(".cart-count"),n=parseInt(e.textContent)||0,a=parseInt(t)||0;e.textContent=n+a}function showPopup(t,e){const n=document.querySelector(".cart-popup"),a=document.querySelector(".variant-info"),o=document.querySelector(".quantity-info");a.textContent=t,o.textContent=e,n.style.display="block",setTimeout((function(){n.style.display="none"}),3e3)}document.getElementById("add-to-cart").addEventListener("click",(function(){const t=document.querySelector('.variant-radio[name="product-variant"]:checked').value,e=document.querySelector(".quantity-input").value,n=document.querySelector('.variant-radio[name="product-variant"]:checked').dataset.variantText;fetch("/cart/add.js",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({id:t,quntity:e,size:n})}).then((t=>t.json())).then((t=>{t.success?(updateCartCount(e),showPopup(n,e)):alert("Error!! Please try again :(")})).catch((t=>{console.error(t)}))}));